script:
frappe.ui.form.on('Order Processing Request', {

    sales_order(frm, cdt, cdn) {
        console.log(frm.doc.sales_order);
        assignQuantities(frm, cdn);
    }

});

frappe.ui.form.on('Quantities Table', {
    straight_sqm: (frm, cdt, cdn) => {
        updateFittingSqm(frm, cdt, cdn);
    },
    total_nos: (frm, cdt, cdn) => {
        updateFittingSqm(frm, cdt, cdn);
    },
    straight_no: (frm, cdt, cdn) => {
        updateFittingNos(frm, cdt, cdn);
    },
    total_duct_pcs: (frm, cdt, cdn) => {
        updateFittingNos(frm, cdt, cdn);
    }
});

function updateFittingSqm(frm, cdt, cdn) {
    let item = locals[cdt][cdn];
    if (item.uom != "sqm") return;
    frappe.model.set_value(cdt, cdn, "fitting_sqm", item.quantity - item.straight_sqm);
    frm.refresh_field("quantities_required");
}

function updateFittingNos(frm, cdt, cdn) {
    let item = locals[cdt][cdn];
    if (item.uom != "sqm") return;
    if(isNaN(item.total_nos) || isNaN(item.straight_no)) return;
    frappe.model.set_value(cdt, cdn, "fitting_no", item.total_nos - item.straight_no);
    frm.refresh_field("quantities_required");
}

function assignQuantities(frm, cdn) {
    frappe.call({
        type: "GET",
        method: "btb_cvs_support.api.opr.get_pending_quantities",
        args: {
            "sales_order_number": frm.doc.sales_order,
            "opr_name": cdn
        },
        callback: function (result) {
            let items = result.message;
            for (var i = 0; i < items.length; i++) {
                if (items[i].custom_manufactured_item) addQuantity(frm, items[i]);
                else addAccessory(frm, items[i]);
            }
            frm.refresh_field("quantities_required");
            frm.refresh_field("accessories");
        }
    })
}

function addQuantity(frm, item) {
    let child = getItem(frm.doc.quantities_required, "item_code", item.item_code);
    if (child !== null) return;
    child = {
        item_code: item.item_code, description: item.description, quantity: item.quantity,
        rate: item.rate, amount: item.rate * item.quantity, so: frm.doc.sales_order,
        uom: item.uom
    };
    frm.add_child("quantities_required", child);

}

function addAccessory(frm, item) {
    let child = getItem(frm.doc.accessories, "item", item.item_code);
    if (child !== null) return;
    child = {
        item: item.item_code, description: item.description, quantity: item.quantity,
        rate: item.rate, amount: item.rate * item.quantity, uom: item.uom
    };
    frm.add_child("accessories", child);

}

function getItem(items, property, value) {
    let result = items.filter(item => item[property] === value);
    if (result.length === 0) return null;
    return result[0];
}
